import { Component } from '@angular/core';
import { LoggingService } from '../logging.sesrvice';
import { AccountsService } from '../accounts.service';

@Component({
  selector: 'app-new-account',
  templateUrl: './new-account.component.html',
  styleUrls: ['./new-account.component.css'],
  providers: [LoggingService], //stap 1
})
export class NewAccountComponent {
  constructor(
    private loggingService: LoggingService, //step 2
    private accountsService: AccountsService
  ) {
    this.accountsService.statusUpdated.subscribe((status: string) => {
      alert('New status:' + status);
    });
  }
  onCreateAccount(accountName: string, accountStatus: string) {
    this.accountsService.addAccount(accountName, accountStatus);
    // this.loggingService.logStatusChange(accountStatus); //step 3
  }
}
<p>new-account works!</p>
<div class="row">
  <div class="col-xs-12 col-md-8 col-md-offset-2">
    <div class="form-group">
      <label>Account Name</label>
      <input type="text" class="form-control" #accountName />
    </div>
    <div class="form-group">
      <select class="form-control" #status>
        <option value="active">Active</option>
        <option value="inactive">Inactive</option>
        <option value="hidden">Hidden</option>
      </select>
    </div>
    <button
      type="button"
      class="btn btn-info"
      (click)="onCreateAccount(accountName.value, status.value)"
    >
      Add Account
    </button>
  </div>
</div>
