import { Component, Input } from '@angular/core';
import { LoggingService } from '../logging.sesrvice';
import { AccountsService } from '../accounts.service';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css'],
  providers: [LoggingService], //step1
})
export class AccountComponent {
  @Input() account!: { name: string; status: string };
  @Input() id!: number;

  constructor(
    private loggingService: LoggingService,
    private accountsService: AccountsService
  ) {} //step2

  onSetTo(status: string) {
    this.accountsService.updateAccount(this.id, status);
    this.loggingService.logStatusChange(status); //step 3
  }
}
<div class="row">
  <div class="col-xs-12 col-md-8 col-md-offset-2">
    <h5>{{ account.name }}</h5>
    <hr />
    <p>This account is {{ account.status }}</p>
    <button class="btn btn-default" (click)="onSetTo('active')">
      Set to 'active'
    </button>
    <button class="btn btn-default" (click)="onSetTo('inactive')">
      Set to 'inactive'
    </button>
    <button class="btn btn-default" (click)="onSetTo('unknown')">
      Set to 'unknown'
    </button>
  </div>
</div>
